import { useState, useEffect } from "react";
import { motion } from "framer-motion";
import { HashLink } from "react-router-hash-link";
import Footer from "./components/Footer";

function App() {
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simulate a short loading time
    setTimeout(() => {
      setLoading(false);
    }, 1000); // 1 second
  }, []);

  if (loading) {
    return (
      <div className="h-screen flex justify-center items-center bg-dark">
        <motion.h1
          className="text-3xl font-bold text-neon-teal"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5 }}
        >
          Loading...
        </motion.h1>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-dark">
      {/* Hero Section */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
        className="container mx-auto px-4 py-20 text-center"
      >
        {/* Headshot */}
        <motion.img
          src="/headshot.jpg"
          alt="Oshadhi Jayaweera"
          className="w-48 h-48 rounded-full border-4 border-neon-teal mx-auto mb-8 shadow-glow"
          whileHover={{ scale: 1.05 }}
        />
        {/* Headline */}
        <motion.h1
          className="text-5xl md:text-6xl font-bold mb-6 bg-gradient-to-r from-neon-teal to-soft-magenta bg-clip-text text-transparent"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.3 }}
        >
          Oshadhi Jayaweera
        </motion.h1>
        {/* Subtitle */}
        <motion.p
          className="text-xl md:text-2xl text-off-white mb-8"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.6 }}
        >
          MBA Graduate | Marketing Strategist | Business Communication Expert
        </motion.p>
        {/* CTA Button */}
        <HashLink
          to="/#contact"
          smooth
          className="bg-neon-teal text-dark px-8 py-3 rounded-lg font-bold text-lg hover:shadow-[0_0_25px_-5px_rgba(0,255,204,0.3)] transition-all"
        >
          Get in Touch
        </HashLink>
      </motion.div>

      {/* Footer */}
      <Footer />
    </div>
  );
}

export default App;
